;            $('#GarageMapInput').removeClass('border border-danger');            var places = searchBox.getPlaces();            console.log(places[0]);            if (places.length == 0) {                $('#GarageMapInput').addClass('border border-danger');                $('#GarageMapLat').val('');                $('#GarageMapLng').val('');                return;            }            // For each place, get the icon, name and location.            var bounds = new google.maps.LatLngBounds();            if (places.length == 1) {                if (!places[0].geometry) {                    $('#GarageMapInput').addClass('border border-danger');                    console.log("Returned place contains no geometry");                    return;                }                $('#GarageMapLat').val(places[0].geometry.location.lat());                $('#GarageMapLng').val(places[0].geometry.location.lng());                var addr=places[0].address_components;                for (var x in addr){                    console.log(addr[x].types);                    if(addr[x].types[0]==="country"){                        $(".s_country option[value='"+ addr[x].long_name +"']").prop("selected",true);                        $(".s_country").prop("disabled",true);                        $("#profileCountry").val(addr[x].long_name);                    }                    else if(addr[x].types[0]==="administrative_area_level_2"){                        $("input[name='city']").val(addr[x].long_name);                        $("#city").val(addr[x].long_name);                    }                }            } else {                $('#GarageMapInput').addClass('border border-danger');                $('#GarageMapLat').val('');                $('#GarageMapLng').val('');                console.log("error in results");                return;            }            places.forEach(function(place) {                if (!place.geometry) {                    console.log("Returned place contains no geometry");                    return;                }                if (place.geometry.viewport) {                    // Only geocodes have viewport.                    bounds.union(place.geometry.viewport);                } else {                    bounds.extend(place.geometry.location);                }            });            map.fitBounds(bounds);        });    }</script><script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyDRThsOQmQXD3F7FpoUnTkAWJtFY3gKCNc&libraries=places&callback=initGarageAutocomplete" async defer></script>